--  DDL for Procedure UPDATE_BLOB_DATA
--  PL/SQL has a string literal limitation of 32767 bytes
--  if the document content is more than 32767, the data should
--  be passed in two parameters p_content1 & p_content2
--------------------------------------------------------
set define off;

create or replace PROCEDURE   "NHINCUSER"."UPDATE_BLOB_DATA"
(
  p_documentid IN NUMBER
, p_content1 IN CLOB,
  p_content2 IN CLOB
) AUTHID CURRENT_USER
  AS
  l_blob          blob;
  l_dest_offset   integer := 1;
  l_source_offset integer := 1;
  l_lang_context  integer := DBMS_LOB.DEFAULT_LANG_CTX;
  l_warning       integer := DBMS_LOB.WARN_INCONVERTIBLE_CHAR;
  v_Content CLOB;
BEGIN
   dbms_lob.createtemporary(v_Content, true);
   dbms_lob.append(v_Content, p_content1);
   dbms_lob.append(v_Content, p_content2);
   
   DBMS_LOB.CREATETEMPORARY(l_blob, TRUE);
   DBMS_LOB.CONVERTTOBLOB( dest_lob    =>l_blob,   src_clob    => v_Content,   amount      =>DBMS_LOB.LOBMAXSIZE,   dest_offset =>l_dest_offset,
                        src_offset  =>l_source_offset,   blob_csid   =>DBMS_LOB.DEFAULT_CSID,   lang_context=>l_lang_context,   warning     =>l_warning);

   update nhincuser.document set rawdata=l_blob where documentid = p_documentid;
   COMMIT;
END UPDATE_BLOB_DATA;
