groovyUtils.setPropertyValue( "Test Case Properties", "correctedRemoteHCID1", correctedRemoteHCID1);
groovyUtils.setPropertyValue( "Test Case Properties", "correctedRemoteHCID2", correctedRemoteHCID2);

