con:configuration.scriptText:
import com.eviware.soapui.support.XmlHolder

def response = new XmlHolder (messageExchange.responseContentAsXml)
response.namespaces["S"] = "http://www.w3.org/2003/05/soap-envelope"
response.namespaces["ns2"] = "urn:hl7-org:v3"

def receiver_device_id = response.getNodeValue("//S:Envelope[1]/S:Body[1]/ns2:RespondingGateway_PRPA_IN201306UV02Response[1]/ns2:communityResponse[1]/ns2:PRPA_IN201306UV02[1]/ns2:receiver[1]/ns2:device[1]/ns2:id[1]/@root")
def receiver_repOrg_id = response.getNodeValue("//S:Envelope[1]/S:Body[1]/ns2:RespondingGateway_PRPA_IN201306UV02Response[1]/ns2:communityResponse[1]/ns2:PRPA_IN201306UV02[1]/ns2:receiver[1]/ns2:device[1]/ns2:asAgent[1]/ns2:representedOrganization[1]/ns2:id[1]/@root")

def sender_device_id = response.getNodeValue("//S:Envelope[1]/S:Body[1]/ns2:RespondingGateway_PRPA_IN201306UV02Response[1]/ns2:communityResponse[1]/ns2:PRPA_IN201306UV02[1]/ns2:sender[1]/ns2:device[1]/ns2:id[1]/@root")
def sender_repOrg_id = response.getNodeValue("//S:Envelope[1]/S:Body[1]/ns2:RespondingGateway_PRPA_IN201306UV02Response[1]/ns2:communityResponse[1]/ns2:PRPA_IN201306UV02[1]/ns2:sender[1]/ns2:device[1]/ns2:asAgent[1]/ns2:representedOrganization[1]/ns2:id[1]/@root")

assert receiver_device_id == receiver_repOrg_id
assert sender_device_id == sender_repOrg_id
assert receiver_device_id == sender_device_id

