def f = new File(springProxyBak)
f.eachLine() {
    line ->
    if(line.contains(aliasLine)){
    		springProxyFile << (aliasReplace + ln);
    }else if(line.contains(propertyLine)){
    		springProxyFile << (propertyReplace + ln);
    }else{
    		springProxyFile << (line + ln);
    }
}

