void DeleteFile(File file, log) {  
   if(file.exists()) {    
      log.info("deleting file " + file);
      file.delete(); 
   } else {
      log.info("file not found, skipping delete for " + file);
   }
}

DeleteFile(backupInternalFile, log);
DeleteFile(backupUddiFile, log);

