def localAA = context.findProperty('LocalAA')
def dqPatientID = context.findProperty('DQPatientID')

context.testCase.setPropertyValue('FullPatientID', "'${dqPatientID}^^^&amp;${localAA}&amp;ISO'");

context.withSql('PatientCorrelationDB') { sql -> 
  def table = context.findProperty('PatientCorrelationTable')

  sql.execute('delete from ' + table)

  def localAA = context.findProperty( "LocalAA" )
  def localPatientID = context.findProperty("LocalPatientID")
  def remoteAA1 = context.findProperty( "RemoteAA1" )
  def remoteAA2 = context.findProperty( "RemoteAA2" )
  def remotePatientID1 = context.findProperty("RemotePatientID1")
  def remotePatientID2 = context.findProperty("RemotePatientID2")
  def insertSql = "insert into " + table + " (correlationId, PatientAssigningAuthorityId, PatientId, CorrelatedPatientAssignAuthId, CorrelatedPatientId) " + 
                   "values (?, ?, ?, ?, ?)"
  sql.executeUpdate(insertSql, [1, localAA, localPatientID, remoteAA1, remotePatientID1])
  sql.executeUpdate(insertSql, [2, localAA, localPatientID, remoteAA2, remotePatientID2])
  //sql.executeUpdate(insertSql, [1, remoteAA1, remotePatientID1, localAA, localPatientID])
  //sql.executeUpdate(insertSql, [2, remoteAA2, remotePatientID2, localAA, localPatientID])
}

