  def remoteHCID = context.findProperty("RemoteHCID")
  def insertSql = "insert into " + table + " (id, assigningauthorityid, homecommunityid) " + 
                   "values (1, ?, ?)"
  sql.executeUpdate(insertSql, [remoteAA, remoteHCID])
}

